(()=>{const e=document.querySelector(".welcome-input"),t=document.querySelector(".welcome-btn"),o=document.querySelector(".player-name"),c=document.querySelector(".computer-name"),r=document.querySelector(".welcome"),n=document.querySelector(".gameplay-text"),l=document.querySelector(".top-text");t.addEventListener("click",(t=>{t.preventDefault(),o.textContent=e.value,o.classList.remove("invisible"),c.classList.remove("invisible"),r.classList.add("hide"),o.value="",n.classList.remove("hide"),l.textContent="Make your move . . ."}));document.querySelectorAll(".right-board > button").forEach((e=>{e.addEventListener("click",(()=>{n.textContent="You fire a shot into enemy waters . . ."}))}))})();